import styled from "styled-components";

export const IntroductionContainer = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
`;

export const TextContainer = styled.div`
  font-family: 'Hammersmith One', sans-serif;
  font-size: 35px;
  position: relative;
  left:30px
  span {
    color: var(--color-secondary)
  }
`;
